CREATE TABLE ATTACHMENT (IDX INTEGER AUTO_INCREMENT NOT NULL, CREATEDDATE DATETIME, DATA LONGBLOB, FILENAME VARCHAR(255), ISDELETED TINYINT(1) default 0, LASTUPDATEDDATE DATETIME, TYPE VARCHAR(255), POST_IDX INTEGER, PRIMARY KEY (IDX))
CREATE TABLE CATEGORY (CODE VARCHAR(255) NOT NULL, ISACTIVE TINYINT(1) default 0, NAME VARCHAR(255), SEQ INTEGER, UNIQCODE VARCHAR(255), p_category_code VARCHAR(255), PRIMARY KEY (CODE))
CREATE TABLE POST (IDX INTEGER AUTO_INCREMENT NOT NULL, CONTENTS LONGTEXT, CREATEDDATE DATETIME, ISDELETED TINYINT(1) default 0, LASTUPDATEDDATE DATETIME, TAG VARCHAR(255), TITLE VARCHAR(255), CATEGORY_CODE VARCHAR(255), PRIMARY KEY (IDX))
ALTER TABLE ATTACHMENT ADD CONSTRAINT FK_ATTACHMENT_POST_IDX FOREIGN KEY (POST_IDX) REFERENCES POST (IDX)
ALTER TABLE CATEGORY ADD CONSTRAINT FK_CATEGORY_p_category_code FOREIGN KEY (p_category_code) REFERENCES CATEGORY (CODE)
ALTER TABLE POST ADD CONSTRAINT FK_POST_CATEGORY_CODE FOREIGN KEY (CATEGORY_CODE) REFERENCES CATEGORY (CODE)
